package kafka;import kafka.javaapi.producer.Producer;import kafka.producer.ProducerConfig;import java.util.Properties;/** * @author YaboSun */public class KafkaProducer {    private String topic;    private Producer<Integer, String> producer;    public KafkaProducer(String topic) {        this.topic = topic;        Properties properties = new Properties();        producer = new Producer<Integer, String>(new ProducerConfig(properties));    }}